$('#codeup').hover(
    function() {
        $(this).css('background-color', '#FF0');
    },
    function() {
        $(this).css('background-color', '#FFF');
    }
);
$('p').dblclick(function(e) {
    $(this).css('font-size', '18px')
});

$('li').hover(
    function() {
        $(this).css('color', '#FF0000');
    },
    function() {
        $(this).css('color', '#000000');
    }

);
